import { Component, OnInit } from '@angular/core';
import { AuthGuard } from '../../utility/app.guard';
import {KeycloakService } from 'keycloak-angular'

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
user:any=""
isLoggedIn=true
  constructor(private KeycloakService:KeycloakService,private AuthGuard:AuthGuard) { }

  ngOnInit(): void {
    this.initializeUserOptions()
    this.userCheck()
  }
  private initializeUserOptions():void{
    this.KeycloakService.loadUserProfile().then(res=>this.user=res.username)

  } 
login(){
  this.KeycloakService.login()

}
logout():void{
  this.KeycloakService.logout()
this.KeycloakService.isLoggedIn().then(res=>this.isLoggedIn=true)
}
async userCheck(){
  this.isLoggedIn=await this.KeycloakService.isLoggedIn()
}
}
<div class="header">
    <a href="/">
        <img src="../../../assets/logo.png" alt="" class="logo" width="100" height="100" />
    </a>

    <div class="btns">
        <a href="#subscribe" class="subscribe-btn">
            Subscribe To Updates
        </a>

        <div>
            <span>Welcome {{user}}</span>
            <button (click)="logout()" class="login-btn" >Logout</button>
        </div>

        <button (click)="login()" class="login-btn" >Login</button>

    </div>    
</div>
